import React, { useEffect,useState } from "react"

interface word{
    id:number,
    word:string,
    pos:string
}
const initialWord = {
    id:0,
    word:'',
    pos:''
}
const btns =['noun','adverb','adjective','verb']
const Speach = () => {
    const [speeches,setSpeeches] = useState<word[]>([initialWord])
    const [index,setIndex] = useState(0)
    const [answer,setAnswer] = useState('')
    const [selectedItem,setSelectedItem] = useState('')



    useEffect(()=>{
        receveData()
        
    },[])

    const receveData = async ()=>{
        const res = await fetch('http://localhost:5000/words')
        const s = await res.json()
        setSpeeches(s)

        console.log(s)
    }
    
    const handleChoose =(item:string) =>{

    item === speeches[index].pos ? setAnswer('correct answer'): setAnswer('wrong answer')
    
    setSelectedItem(item)
    setTimeout(()=>{
        setIndex(index+1)
        setSelectedItem('')
    },5000)
    


    }
    return(
        <>
       <p>{speeches[index].word}</p>
       <p>{answer}</p>
       {btns.map((item:string)=>(
            <button style={item===selectedItem?{backgroundColor:'green'}:{backgroundColor:'red'}} onClick={()=>handleChoose(item)}>
                {item}
            </button>

       ))
        

       }
       
        </>
    )
}

export default Speach
