interface word{
    id:number,
    word:string,
    pos:string
}
const initialWord = {
    id:0,
    word:'',
    pos:''
}
const Speach = () => {
    const [speeches,setSpeeches] = useState([])
    const [renderSpeech ,setRenderSpeech] = useState<word>(initialWord)
    const [index,setIndex] = useState(0)



    useEffect(()=>{
        receveData()
        
    },[])

    const receveData = async ()=>{
        const res = await fetch('http://localhost:5000/words')
        const s = await res.json()
        setSpeeches(s)
        setRenderSpeech(s[0])

        console.log(s)
      }
    
      const handleChoose =(e:React.FormEvent) =>{
        console.log(e.target)


      }
    return(
        <>
       <p>{renderSpeech?.word}</p>
        <button onClick={(e)=>handleChoose(e)}>noun</button>
        <button onClick={(e)=>handleChoose(e)}>adverb</button>
        <button onClick={(e)=>handleChoose(e)}>adjective</button>
        <button onClick={(e)=>handleChoose(e)}>verb</button>
        </>
    )
}

export default Speach
