import { useEffect,useState } from "react"

interface word{
    id:number,
    word:string,
    pos:string
}
const Speach = () => {
    const [speeches,setSpeeches] = useState([])
    const [renderSpeech ,setRenderSpeech] = useState<word>({})
    const [index,setIndex] = useState(0)



    useEffect(()=>{
        receveData()
        setRenderSpeech(speeches[index])

    },[])

    const receveData = async ()=>{
        const res = await fetch('http://localhost:5000/words')
        const s = await res.json()
        setSpeeches(s)

        console.log(s)
      }
    return(
        <>
       <p>{renderSpeech.word}</p>
        <button>noun</button>
        <button>adverb</button>
        <button>adjective</button>
        <button>verb</button>
        </>
    )
}

export default Speach
