import React, { useEffect,useState } from "react"
import {useNavigate} from "react-router-dom"
import ProgressBar from "../components/progress"
interface word{
    id:number,
    word:string,
    pos:string
}
const initialWord = {
    id:0,
    word:'',
    pos:''
}
const btns =['noun','adverb','adjective','verb']

const Speach = () => {
    const [speeches,setSpeeches] = useState<word[]>([initialWord])
    const [index,setIndex] = useState(0)
    const [score,setScore] = useState(0)

    const navigate = useNavigate()

    useEffect(()=>{
        receiveData()       
    },[])

    const receiveData = async ()=>{
        const res = await fetch('http://localhost:4000/words')
        const s = await res.json()
        setSpeeches(s)

        console.log(s)   
    }
    
    const handleChoose =(item:string,e:React.FormEvent) =>{
      const ele = e.target as HTMLButtonElement
      const buttons = document.querySelectorAll('button')
      const correctAnswer = speeches[index].pos;

      resetButtonsStyle(buttons,true)
      ele.disabled =false
      
      /* check if answer is correct or wrong */
      if(item === correctAnswer){
        ele.setAttribute('style','background-color:green')
        setScore(score+10)
      }
      else{
        const correctElement = [...buttons].find(
          (item: HTMLButtonElement) =>
              item.innerHTML === correctAnswer
          );
        correctElement?.setAttribute('style','background-color:green')
        ele.setAttribute('style','background-color:red')
      }
  
      /* change word */
      changeToNextWord(ele,buttons)
       
    }

    const changeToNextWord = (element:HTMLButtonElement,buttons:NodeListOf<HTMLButtonElement>)=>{

      setTimeout(() => {
        /* navigate to rank page if words finished */
        if (index === 9) {
          navigate("/score-rank", { state: { score } });
        }
        setIndex(index + 1);
        resetButtonsStyle(buttons, false);
      }, 2000);

    }

    const resetButtonsStyle = (buttons:NodeListOf<HTMLButtonElement> , value:boolean)=>{
      buttons.forEach((item)=>{
          item.disabled = value
          item.setAttribute("style", "background-color:none");
      })
    }
    return (
      <div className="bg--container h-[100vh]">
        <div className="relative w-full flex flex-col items-center">
          <h1 className="text-white text-7xl mt-[25vh]">
            {speeches[index].word}
          </h1>

          <ProgressBar value={(index / 10) * 100} />

          <div className="flex flex-wrap justify-center mt-20">
            {btns.map((item: string) => (
              <div className="min-h-[50px]  min-[400px]:mr-4 rounded-[50px] mb-4 h-auto min-w-[130px] bg-gradient-to-r from-[#f47c34] to-[#e03f97]">
                <button
                  className="text-white text-xl h-[56px] rounded-[50px] w-[126px] m-[3px] bg-[#2d4a69]"
                  onClick={(e) => handleChoose(item, e)}
                >
                  {item}
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
}

export default Speach
